mapper.select:


     and pgo.dept_id = #{deptId}
     and pgo.user_id = #{userId}
     and pgo.project_id = #{projectId}
     and pgo.gather_month = #{gatherMonth}
     and pgo.total_score = #{totalScore}
     and pgo.gather_status = #{gatherStatus}
     and pgo.gather_id = #{gatherId}
     and pgo.audit_id = #{auditId}
     and pgo.audit_time = #{auditTime}
     and u.user_name like concat('%', #{userName}, '%')
     and d.dept_name like concat('%', #{deptName}, '%')
     and eparam.param_rank = #{paramRank}
     and eparam.param_post = #{paramPost}
     and vp.post_name like concat('%', #{postName}, '%')
    ${params.dataScope}

group by pgo.user_id, pgo.gather_month, pgo.dept_id, pgo.total_score, pgo.create_time,
    u.user_name, d.dept_name, eparam.param_rank, eparam.param_post
order by pgo.user_id asc, u.number asc, pgo.dept_id asc, pgo.create_time desc



     and pgo.dept_id = #{deptId}
     and pgo.user_id = #{userId}
