mapper.select:
SELECT user_id, max( dept_id ) dept_id, concat( min( gather_date ), ' - ', max( gather_date )) gather_date, max( user_name ) user_name, max( dept_name ) dept_name, max( post_name ) post_name, sum( total_score ) total_score FROM ( SELECT pgo.user_id, pgo.dept_id, pgo.gather_date, pgo.total_score, pgo.create_time, u.user_name, d.dept_name, eparam.param_rank, eparam.param_post, group_concat( vp.post_name SEPARATOR '、' ) AS post_name FROM ( SELECT dept_id, user_id, gather_date, sum( total_score ) total_score, max( create_time ) create_time FROM perf_gather_overview
and gather_date >= #{startDate} and gather_date <= #{endDate} and gather_date = #{gatherDate} and user_id = #{userId} and project_id = #{projectId} and total_score = #{totalScore} and gather_status = #{gatherStatus} and gather_id = #{gatherId} and audit_id = #{auditId} and audit_time = #{auditTime}
GROUP BY user_id, dept_id, gather_date ) pgo LEFT JOIN sys_user u ON pgo.user_id = u.user_id LEFT JOIN sys_dept d ON pgo.dept_id = d.dept_id LEFT JOIN vip_employee_param eparam ON pgo.user_id = eparam.user_id LEFT JOIN vip_employee_post epost ON pgo.user_id = epost.user_id LEFT JOIN vip_post vp ON epost.post_id = vp.post_id
and u.user_name like concat('%', #{userName}, '%') and (d.dept_id = #{deptId} or d.parent_id = #{deptId} or d.ancestors like concat('%', #{deptId},',%')) and d.dept_name like concat('%', #{deptName}, '%') and eparam.param_rank = #{paramRank} and eparam.param_post = #{paramPost} and vp.post_name like concat('%', #{postName}, '%') and vp.post_id = #{postId} ${params.dataScope}
GROUP BY pgo.user_id, pgo.gather_date, pgo.dept_id, pgo.total_score, pgo.create_time, u.user_name, d.dept_name, eparam.param_rank, eparam.param_post ORDER BY pgo.user_id ASC, u.number ASC, pgo.gather_date ASC, pgo.dept_id ASC ) tb1 GROUP BY user_id

SELECT pgo.overview_id, pgo.dept_id, pgo.user_id, pgo.project_id, pgo.gather_date, pgo.total_score, pgo.remark, vp.post_name post_name, u.user_name user_name, d.dept_name dept_name FROM ( SELECT project_id, MAX(gather_date) gather_date, MAX(overview_id) overview_id, MAX(dept_id) dept_id, MAX(user_id) user_id, SUM(total_score) total_score, MAX(remark) remark FROM perf_gather_overview
and gather_date >= #{startDate} and gather_date <= #{endDate} and gather_date = #{gatherDate} and user_id = #{userId}
GROUP BY project_id ) pgo left join perf_ind_project pip on pgo.project_id = pip.project_id LEFT JOIN sys_user u ON pgo.user_id = u.user_id left join sys_dept d on pgo.dept_id = d.dept_id left join vip_post vp on pip.post_id = vp.post_id

SELECT pgo.overview_id, pgo.dept_id, pgo.user_id, pgo.project_id, pgo.gather_date, pgo.total_score, pgo.remark, vp.post_name post_name, u.user_name user_name, d.dept_name dept_name FROM ( SELECT gather_date, MAX(project_id) project_id, MAX(overview_id) overview_id, MAX(dept_id) dept_id, MAX(user_id) user_id, SUM(total_score) total_score, MAX(remark) remark FROM perf_gather_overview
and gather_date >= #{startDate} and gather_date <= #{endDate} and gather_date = #{gatherDate} and user_id = #{userId}
GROUP BY gather_date ) pgo left join perf_ind_project pip on pgo.project_id = pip.project_id LEFT JOIN sys_user u ON pgo.user_id = u.user_id left join sys_dept d on pgo.dept_id = d.dept_id left join vip_post vp on pip.post_id = vp.post_id

SELECT AVG(pgo.total_score) AS avg_score
FROM perf_gather_overview pgo
WHERE pgo.gather_date >= #{startDate}
  AND pgo.gather_date <= #{endDate}

SELECT pgo.dept_id, pgo.gather_date, AVG(pgo.total_score) AS avg_score FROM perf_gather_overview pgo WHERE pgo.gather_date >= #{startDate} AND pgo.gather_date <= #{endDate} AND pgo.dept_id IN
#{id}
GROUP BY pgo.dept_id, pgo.gather_date ORDER BY pgo.dept_id, pgo.gather_date
